object.script:
function gn(n)
	return input.getNumber(n)
end

function onTick()
	collective=gn(1)*100
	throttle=gn(2)
end

function onDraw()
	screen.setColor(0,255,0)
	screen.drawText(0,0, string.format("POWER\n%4.0f\n\nPITCH\n%4.0f%%",throttle,collective))
end

zooms={
	1000,
	2000,
	4000,
	8000,
	10000
}

function gn(n)
	return input.getNumber(n)
end

function sn(n,v)
	output.setNumber(n,v)
end

function gb(n)
	return input.getBool(n)
end

function sb(n,v)
	output.setBool(n,v)
end

function onTick()
	local i
	local z=gn(32)

	for i=1,32 do
		sn(i,gn(i))
		sb(i,gb(i))
	end

	sn(32, zooms[math.floor(z)] )
end


function onDraw()
end
